import React, { useState,useEffect } from 'react';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "../components/ui/select";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "../components/ui/table";
import { useQuery } from "@tanstack/react-query";
import { useLocation } from 'react-router-dom';


// Spinner component for loading state
const LoadingSpinner = () => (
  <div className="flex justify-center items-center">
    <div className="w-8 h-8 border-4 border-t-4 border-green-500 rounded-full animate-spin"></div>
  </div>
);

// Mock API calls - replace these with actual API endpoints
const fetchOptions = async () => {
  // Simulate API call
  return [
    { id: 1, label: "January 2025 Report" },
    { id: 2, label: "February 2025 Report" },
    { id: 3, label: "March 2025 Report" }
  ];
};

const fetchReportData = async (reportId) => {
  if (!reportId) return [];
  return [
    { id: 1, name: "John Doe", amount: 1000, status: "Paid" },
    { id: 2, name: "Jane Smith", amount: 2000, status: "Pending" },
    { id: 3, name: "Bob Johnson", amount: 1500, status: "Paid" }
  ];
};

const ReportPage = () => {
  const { state } = useLocation();
  const [selectedReport, setSelectedReport] = useState(null);
  const [data, setData] = useState(null); // Store the API data
  const [isLoading, setIsLoading] = useState(true); // Track the loading state
  const [error, setError] = useState(null); // Track any errors

  const fileName = state?.fileName
  console.log("file :",fileName)

  useEffect(() => {
    if (fileName) {
      const fetchReportData = async () => {
        setIsLoading(true);
        try {
          // Assuming the API is expecting a POST request with a JSON body
          const response = await axios.post('http://127.0.0.1:5000/start-process', {
            fileName: fileName,
          });
          console.log('API response:', response.data);
          setReportData(response.data); // Store the response data
        } catch (error) {
          console.error('Error fetching report data:', error);
        } finally {
          setIsLoading(false);
        }
      };

      fetchReportData();
    }
  }, [fileName]);

  const { data: options = [], isLoading: optionsLoading, error: optionsError } = useQuery({
    queryKey: ['reportOptions'],
    queryFn: fetchOptions
  });

  const { data: reportData = [], isLoading: reportLoading, error: reportError } = useQuery({
    queryKey: ['reportData', selectedReport],
    queryFn: () => fetchReportData(selectedReport),
    enabled: !!selectedReport // Only fetch if a report is selected
  });

  return (
    <div className="min-h-screen bg-gradient-to-br from-emerald-600 to-teal-700">
      <div className="container mx-auto px-4 py-8">
        <h1 className="text-3xl md:text-4xl font-bold text-white mb-8">
          Crediflow report
        </h1>

        <div className="bg-white/95 rounded-lg shadow-xl p-6">
          <div className="max-w-md mb-8">
            {/* Select dropdown for report */}
            <Select value={selectedReport} onChange={setSelectedReport}>
              <SelectTrigger className="w-full">
                <SelectValue placeholder="Select a report" />
              </SelectTrigger>
              <SelectContent>
                {optionsLoading ? (
                  <SelectItem disabled>Loading reports...</SelectItem>
                ) : optionsError ? (
                  <SelectItem disabled>Error loading reports</SelectItem>
                ) : (
                  options.map((option) => (
                    <SelectItem key={option.id} value={option.id.toString()}>
                      {option.label}
                    </SelectItem>
                  ))
                )}
              </SelectContent>
            </Select>
          </div>

          {reportLoading ? (
            <div className="text-center text-white">
              <p>Loading report data...</p>
            </div>
          ) : reportError ? (
            <div className="text-center text-red-600">
              <p>Error loading report data. Please try again later.</p>
            </div>
          ) : selectedReport && reportData.length > 0 ? (
            <div className="overflow-x-auto">
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Name</TableHead>
                    <TableHead>Amount</TableHead>
                    <TableHead>Status</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {reportData.map((row) => (
                    <TableRow key={row.id}>
                      <TableCell>{row.name}</TableCell>
                      <TableCell>${row.amount}</TableCell>
                      <TableCell>{row.status}</TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </div>
          ) : (
            <div className="text-center text-white">
              <p>No data available for the selected report.</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ReportPage;
