import React, { useState, useEffect } from 'react';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "../components/ui/select";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "../components/ui/table";
import { useQuery } from "@tanstack/react-query";
import { useLocation } from 'react-router-dom';
import axios from 'axios';


const fetchOptions = async () => {
  return [
    { id: 1, label: "January 2025 Report" },
    { id: 2, label: "February 2025 Report" },
    { id: 3, label: "March 2025 Report" }
  ];
};

const ReportPage = () => {
  
  const [reportData, setReportData] = useState(null);
  const [error, setError] = useState(null);

  const { state } = useLocation();
  const { myData } = state || {};

  const [selectedReport, setSelectedReport] = useState('');
  const [reportKeys, setReportKeys] = useState([]);


  useEffect(() => {
    if (myData) {
      const keys = Object.keys(myData);
      setReportKeys(keys);
    }
  }, [myData]);

  const { data: options = [], isLoading: optionsLoading, error: optionsError } = useQuery({
    queryKey: ['reportOptions'],
    queryFn: fetchOptions
  });



  return (
    <div className="min-h-screen bg-gradient-to-br from-emerald-600 to-teal-700">
      <div className="container mx-auto px-4 py-8">
        <h1 className="text-3xl md:text-4xl font-bold text-white mb-8">
          Crediflow report
        </h1>

        <div className="bg-white/95 rounded-lg shadow-xl p-6">
          <div className="max-w-md mb-8">
          <Select value={selectedReport} onValueChange={setSelectedReport}>
          <SelectTrigger className="w-full">
            <SelectValue placeholder="Select a report" />
          </SelectTrigger>
          <SelectContent>
            {reportKeys.length === 0 ? (
              <SelectItem disabled>Loading reports...</SelectItem>
            ) : (
              reportKeys.map((key) => (
                <SelectItem key={key} value={key}>
                  {key}
                </SelectItem>
              ))
            )}
          </SelectContent>
        </Select>

          </div>

          {isGlobalLoading ? (
            <div className="text-center text-white">
              <LoadingSpinner />
            </div>
          ) : error ? (
            <div className="text-center text-red-600">
              <p>{error}</p>
            </div>
          ) : selectedReport && myData[selectedReport] && myData[selectedReport].length > 0 ? (
            <div className="overflow-x-auto">
              <Table>
                <TableHeader>
                  <TableRow>
                    {/* Dynamically generate columns */}
                    {Object.keys(myData[selectedReport][0]).map((field) => (
                      <TableHead key={field}>{field.charAt(0).toUpperCase() + field.slice(1)}</TableHead>
                    ))}
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {/* Dynamically generate rows */}
                  {myData[selectedReport].map((row, index) => (
                    <TableRow key={index}>
                      {Object.values(row).map((value, idx) => (
                        <TableCell key={idx}>
                          {typeof value === 'number' ? `$${value}` : value}
                        </TableCell>
                      ))}
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </div>
          ) : (
            <div className="text-center text-white">
              <p>No data available for the selected report.</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ReportPage;
