import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "../components/ui/table";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "../components/ui/select";

const ReportPage = () => {
  const [selectedReport, setSelectedReport] = useState('');
  const [tableColumns, setTableColumns] = useState([]);
  const { state } = useLocation();
  const { myData } = state || {};

  useEffect(() => {
    if (selectedReport && myData[selectedReport]) {
      const columnsSet = new Set();

      const selectedData = myData[selectedReport];

      if (Array.isArray(selectedData)) {
        selectedData.forEach(item => {
          Object.keys(item || {}).forEach(key => columnsSet.add(key));
        });
      } else if (typeof selectedData === 'object') {
        Object.keys(selectedData || {}).forEach(key => columnsSet.add(key));
      }

      setTableColumns(Array.from(columnsSet));
    }
  }, [selectedReport, myData]);

  return (
    <div className="min-h-screen bg-gradient-to-br from-emerald-600 to-teal-700">
      <div className="container mx-auto px-4 py-8">
        <h1 className="text-3xl md:text-4xl font-bold text-white mb-8">Crediflow report</h1>

        <div className="bg-white/95 rounded-lg shadow-xl p-6">
          <div className="max-w-md mb-8">
            <Select value={selectedReport} onValueChange={(value) => setSelectedReport(value)}>
              <SelectTrigger className="w-full">
                <SelectValue placeholder="Select a report" />
              </SelectTrigger>
              <SelectContent>
                {myData && Object.keys(myData).map((key) => (
                  <SelectItem key={key} value={key}>
                    {key}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          {selectedReport && myData[selectedReport] ? (
            <div className="overflow-x-auto">
              <Table>
                <TableHeader>
                  <TableRow>
                    {tableColumns.map((col) => (
                      <TableHead key={col}>{col}</TableHead>
                    ))}
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {Array.isArray(myData[selectedReport])
                    ? myData[selectedReport].map((row, index) => (
                        <TableRow key={index}>
                          {tableColumns.map((col) => (
                            <TableCell key={col}>
                              {typeof row[col] === 'object'
                                ? JSON.stringify(row[col])
                                : row[col] ?? "-"}
                            </TableCell>
                          ))}
                        </TableRow>
                      ))
                    : (
                      <TableRow>
                        {tableColumns.map((col) => (
                          <TableCell key={col}>
                            {typeof myData[selectedReport][col] === 'object'
                              ? JSON.stringify(myData[selectedReport][col])
                              : myData[selectedReport][col] ?? "-"}
                          </TableCell>
                        ))}
                      </TableRow>
                    )}
                </TableBody>
              </Table>
            </div>
          ) : (
            <div className="text-center text-white">
              <p>No data available for the selected report.</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ReportPage;
