import React, { useState } from 'react';
// import { Button } from "@/components/ui/button";
// import { Input } from "@/components/ui/input";
// import { Card } from "@/components/ui/card";

import {Button} from '../components/ui/button';
import {Input} from '../components/ui/input';
import {Card} from '../components/ui/card';



const LoginPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle login logic here
    console.log('Login attempt with:', { username, password });
  };

  return (
    <div className="min-h-screen flex items-center justify-between p-8 bg-gradient-to-br from-emerald-600 to-teal-700">
      <div className="w-1/2">
        <h1 className="text-7xl font-bold text-white">Welcome!</h1>
      </div>
      
      <Card className="w-[400px] p-6 shadow-2xl bg-white/95 rounded-3xl">
        <form onSubmit={handleSubmit} className="space-y-4">
          <Input
            type="text"
            placeholder="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            className="h-12 px-4 rounded-xl border-gray-200"
          />
          
          <Input
            type="password"
            placeholder="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="h-12 px-4 rounded-xl border-gray-200"
          />
          
          <Button
            type="submit"
            className="w-full h-12 text-white bg-blue-600 hover:bg-blue-700 rounded-xl text-lg font-medium"
          >
            login
          </Button>
        </form>
      </Card>
    </div>
  );
};

export default LoginPage;
