import React, { useState, useEffect } from 'react';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "../components/ui/select";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "../components/ui/table";
// import { useQuery } from "@tanstack/react-query";
import { useLocation } from 'react-router-dom';
import axios from 'axios';


const fetchOptions = async () => {
  return [
    { id: 1, label: "January 2025 Report" },
    { id: 2, label: "February 2025 Report" },
    { id: 3, label: "March 2025 Report" }
  ];
};

const ReportPage = () => {
  
  // const [reportData, setReportData] = useState(null);
  // const [error, setError] = useState(null);

  const { state } = useLocation();
  const { myData } = state || {};

  const [selectedReport, setSelectedReport] = useState('');
  const [reportKeys, setReportKeys] = useState([]);


  useEffect(() => {
    if (myData) {
      const keys = Object.keys(myData);
      setReportKeys(keys);
    }
  }, [myData]);


  console.log('Selected Report Data:', myData[selectedReport]);

  return (
    <div className="min-h-screen bg-gradient-to-br from-emerald-600 to-teal-700">
      <div className="container mx-auto px-4 py-8">
        <h1 className="text-3xl md:text-4xl font-bold text-white mb-8">
          Crediflow report
        </h1>

        <div className="bg-white/95 rounded-lg shadow-xl p-6">
          <div className="max-w-md mb-8">

          <Select value={selectedReport} onValueChange={setSelectedReport}>
          <SelectTrigger className="w-full">
            <SelectValue placeholder="Select a report" />
          </SelectTrigger>
          <SelectContent>
            {myData && Object.keys(myData).map((key) => (
              <SelectItem key={key} value={String(key)}>
                {key}
              </SelectItem>
            ))}
          </SelectContent>
        </Select>

          </div>

          

          {selectedReport && myData[selectedReport] ? (
  <div className="overflow-x-auto">
    <Table>
      <TableHeader>
        <TableRow>
          <TableHead>Item</TableHead>
          <TableHead>2020</TableHead>
          <TableHead>2019</TableHead>
        </TableRow>
      </TableHeader>
      <TableBody>
        {/* Handle nested objects */}
        {Object.entries(myData[selectedReport]).map(([itemName, itemValue]) => {
          if (typeof itemValue === "object" && !Array.isArray(itemValue)) {
            // If it's nested like "Non-current assets"
            return Object.entries(itemValue).map(([subItemName, subItemValue]) => (
              <TableRow key={itemName + subItemName}>
                <TableCell className="font-medium">{subItemName}</TableCell>
                <TableCell>{subItemValue?.["2020"] ?? "-"}</TableCell>
                <TableCell>{subItemValue?.["2019"] ?? "-"}</TableCell>
              </TableRow>
            ));
          } else {
            // Normal field
            return (
              <TableRow key={itemName}>
                <TableCell className="font-medium">{itemName}</TableCell>
                <TableCell>{itemValue?.["2020"] ?? "-"}</TableCell>
                <TableCell>{itemValue?.["2019"] ?? "-"}</TableCell>
              </TableRow>
            );
          }
        })}
      </TableBody>
    </Table>
  </div>
)  : (
            <div className="text-center text-white">
              <p>No data available for the selected report.</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ReportPage;
