import React, { useState, useEffect } from 'react';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "../components/ui/select";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "../components/ui/table";
import { useQuery } from "@tanstack/react-query";
import { useLocation } from 'react-router-dom';
import axios from 'axios';

// Spinner component for loading state with larger size and gradient
// Spinner component for loading state with a larger, circular spinner and gradient
const LoadingSpinner = () => (
  <div className="flex justify-center items-center">
    <div 
      className="w-16 h-16 border-8 border-t-8 border-transparent rounded-full animate-spin"
      style={{
        borderTopColor: 'linear-gradient(to right, #38b000, #00b8d9, #00f3a3)',
        borderRightColor: 'transparent',
        borderBottomColor: 'transparent',
        borderLeftColor: 'transparent'
      }} 
    />
  </div>
);



const fetchOptions = async () => {
  return [
    { id: 1, label: "January 2025 Report" },
    { id: 2, label: "February 2025 Report" },
    { id: 3, label: "March 2025 Report" }
  ];
};

const ReportPage = () => {
  const { state } = useLocation();
  const [selectedReport, setSelectedReport] = useState(null);
  const [reportData, setReportData] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const fileName = state?.fileName;
  console.log("file :", fileName);

  useEffect(() => {
    if (fileName) {
      const fetchReportData = async () => {
        setIsLoading(true);
        try {
          const response = await axios.post('http://127.0.0.1:5000/start-process', {
            fileName: fileName,
          });
          setReportData(response.data);
        } catch (error) {
          setError("Error fetching report data");
        } finally {
          setIsLoading(false);
        }
      };

      fetchReportData();
    }
  }, [fileName]);

  const { data: options = [], isLoading: optionsLoading, error: optionsError } = useQuery({
    queryKey: ['reportOptions'],
    queryFn: fetchOptions
  });

  const isGlobalLoading = optionsLoading || isLoading;

  return (
    <div className="min-h-screen bg-gradient-to-br from-emerald-600 to-teal-700">
      <div className="container mx-auto px-4 py-8">
        <h1 className="text-3xl md:text-4xl font-bold text-white mb-8">
          Crediflow report
        </h1>

        <div className="bg-white/95 rounded-lg shadow-xl p-6">
          <div className="max-w-md mb-8">
            <Select value={selectedReport} onChange={setSelectedReport}>
              <SelectTrigger className="w-full">
                <SelectValue placeholder="Select a report" />
              </SelectTrigger>
              <SelectContent>
                {optionsLoading ? (
                  <SelectItem disabled>Loading reports...</SelectItem>
                ) : optionsError ? (
                  <SelectItem disabled>Error loading reports</SelectItem>
                ) : (
                  options.map((option) => (
                    <SelectItem key={option.id} value={option.id.toString()}>
                      {option.label}
                    </SelectItem>
                  ))
                )}
              </SelectContent>
            </Select>
          </div>

          {isGlobalLoading ? (
            <div className="text-center text-white">
              <LoadingSpinner />
            </div>
          ) : error ? (
            <div className="text-center text-red-600">
              <p>{error}</p>
            </div>
          ) : selectedReport && reportData && reportData.length > 0 ? (
            <div className="overflow-x-auto">
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Name</TableHead>
                    <TableHead>Amount</TableHead>
                    <TableHead>Status</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {reportData.map((row) => (
                    <TableRow key={row.id}>
                      <TableCell>{row.name}</TableCell>
                      <TableCell>${row.amount}</TableCell>
                      <TableCell>{row.status}</TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </div>
          ) : (
            <div className="text-center text-white">
              <p>No data available for the selected report.</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ReportPage;
